<?php
    // TODO : use parameters.

    function isValidMachineSubmit() {
        if(!isset($_POST['codeMachine']) || !isValidCodeMachine($_POST['codeMachine']))
            return false;

        if(!isset($_POST['shortLabel']) || !isValidShortLabel($_POST['shortLabel']))
            return false;

        if(!isset($_POST['longLabel']) || !isValidLongLabel($_POST['longLabel']))
            return false;

        if(!isset($_POST['machineUsePrice']) || !isValidMachineUsePrice($_POST['machineUsePrice']))
            return false;

        if(!isset($_POST['serialNumber']) || !isValidSerialNumber($_POST['serialNumber']))
            return false;

        if(!isset($_POST['manufacturer']) || !isValidManufacturer($_POST['manufacturer']))
            return false;

        if(!isset($_POST['comment']) || !isValidComment($_POST['comment']))
            return false;

        if(!isset($_POST['docLink1']) || !isValidDocLink($_POST['docLink1']))
            return false;

        if(!isset($_POST['docLink2']) || !isValidDocLink($_POST['docLink2']))
            return false;

        return true;
    }

    function isValidCodeMachine($codeMachine) {
        global $DB_DB;

        $request = $DB_DB->prepare('SELECT machineLabel FROM Machine');
        $request->execute();

        foreach($request->fetchAll() as $row)
            if($row['machineLabel'] == $codeMachine)
                return false;

        return true;
    }

    function isValidShortLabel($shortLabel) {
        return true;
    }

    function isValidLongLabel($longLabel) {
        return true;
    }

    function isValidMachineUsePrice($machineUsePrice) {
        if(is_numeric($machineUsePrice) && $machineUsePrice > 0)
            return true;
        return false;
    }

    function isValidSerialNumber($serialNumber) {
        return true;
    }

    function isValidManufacturer($manufacturer) {
        return true;
    }

    function isValidComment($comment) {
        return true;
    }

