        }
    }

    function deleteMachine($idDelete) {
        global $DB_DB;
        $request = $DB_DB->prepare('DELETE FROM Machine WHERE idMachine = :idMachine');

        try {
            $request->execute(array(
                'idMachine' => $idDelete
				));
        }
        catch(Exception $e) {
            echo $e;
        }
    }

